-- Populate author table
INSERT INTO `author` (`author_id`, `author_name`, `author_description`, `author_avatar`) VALUES
       (1, 'Amelia Kingston', 'Amelia Kingston là một nhà văn người Mỹ nổi tiếng với những tác phẩm tình yêu đầy cảm xúc và hài hước. Cô được biết đến với việc viết những câu chuyện lãng mạn, nhẹ nhàng nhưng cũng đầy sức mạnh và cảm hứng. Những tác phẩm của Amelia thường kể về cuộc sống hàng ngày của những người trẻ, đánh giá cao tình yêu và mối quan hệ gia đình. Cô được đánh giá cao về việc tạo ra các nhân vật sống động và nội dung hấp dẫn, làm cho độc giả mê mẩn và không thể bỏ qua các tác phẩm của cô.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247908/authors/u1dxhrf2hngx4g4tbe5y.jpg'),
       (2, 'Victor Sinclair', 'Victor Sinclair là một nhà văn kinh dị nổi tiếng từ Anh. Ông nổi tiếng với những tác phẩm kinh dị căng thẳng và đầy ám ảnh. Victor là một cây bút tài năng trong việc tạo ra bầu không khí u ám và sợ hãi trong truyện của mình. Ông đã tạo nên những nhân vật khắc sâu và những tình tiết bất ngờ, khiến độc giả không thể rời mắt khỏi trang sách. Victor Sinclair đã tạo dựng một thế giới kinh dị riêng biệt và trở thành một trong những nhà văn kinh dị hàng đầu của thời đại.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247908/authors/hcbdypasmuco5chxr0fk.jpg'),
       (3, 'Isabella Moreno', 'Mô tả: Isabella Moreno là một nhà văn người Tây Ban Nha nổi tiếng với những tác phẩm lịch sử và phiêu lưu hấp dẫn. Cô nổi tiếng với việc tạo ra những cốt truyện phức tạp và tường thuật tinh tế, đưa độc giả trở lại thời kỳ hào hùng và các cuộc phiêu lưu thú vị. Isabella có khả năng vẽ nét chi tiết trong các môi trường lịch sử và xây dựng nhân vật đa chiều. Tác phẩm của cô thường kết hợp giữa hành trình mạo hiểm và yếu tố tình cảm, tạo nên những câu chuyện đáng nhớ và gợi cảm xúc sâu sắc.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247908/authors/iwtqch6u7f3sbwkkrgai.jpg'),
       (4, 'Gabriel Montgomery', 'Gabriel Montgomery là một nhà văn người Pháp với những tác phẩm về cuộc sống hiện đại và xã hội đương đại. Gabriel được biết đến với việc viết về những vấn đề xã hội nhức nhối như bạo lực gia đình, đấu tranh cho quyền bình đẳng giới và tầm quan trọng của tình yêu thương. Những tác phẩm của Gabriel thường mang thông điệp sâu sắc và đánh thức ý thức xã hội của độc giả.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247908/authors/nft4zxzkcouhqbtymm6k.jpg'),
       (5, 'Alessandra Rossi', 'Alessandra Rossi là một nhà văn người Ý nổi tiếng với những tác phẩm về tình yêu và gia đình. Cô tạo ra những câu chuyện lãng mạn, đậm chất nước Ý, kể về những tình yêu đầy cảm xúc và những mối quan hệ phức tạp. Alessandra được biết đến với cách viết đầy tình cảm và mô tả tinh tế về đời sống Ý và văn hóa Ý.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247907/authors/cajtv0tckucq1zqgnykb.jpg'),
       (6, 'Maya Patel', 'Maya Patel là một nhà văn người Ấn Độ gốc Mỹ nổi tiếng với những tác phẩm về chuyến hành trình tìm kiếm bản thân và gắn kết gia đình. Cô tạo ra những câu chuyện đầy màu sắc về cuộc sống ở Ấn Độ và di cư sang Mỹ, thể hiện sự đan xen của hai nền văn hóa. Tác phẩm của Maya thường kể về sự vượt qua khó khăn, khám phá bản thân và tìm kiếm sự thỏa mãn trong cuộc sống.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247907/authors/occ23jq2kcrl9bx5iylf.jpg'),
       (7, 'Isla Bennett', 'Isla Bennett là một nhà văn người Scotland với những tác phẩm về thần thoại và huyền bí. Cô tạo ra những câu chuyện kỳ diệu về các sinh vật huyền bí, hồn ma và thế giới siêu nhiên. Isla được biết đến với việc tạo nên những thế giới độc đáo và mê hoặc độc giả bằng trí tưởng tượng phong phú và lối viết tinh tế.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247907/authors/jvg5djwe2ywbqokkvbh5.jpg'),
       (8, 'Amara Rahman', 'Amara Rahman là một nhà văn người Pakistan với những tác phẩm về cuộc sống và văn hóa Pakistan. Cô tạo ra những câu chuyện đầy sự mê hoặc và tình cảm, khám phá các mặt khác nhau của cuộc sống trong một xã hội đa dạng và phức tạp. Amara nổi tiếng với việc mô tả chân thực về cuộc sống hàng ngày, các mối quan hệ và vấn đề xã hội trong nền văn hóa Pakistan.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247907/authors/fqjvtcyrysq6kuo6jd18.jpg'),
       (9, 'Eva Johansson', 'Eva Johansson là một nhà văn người Thụy Điển với những tác phẩm về hành trình khám phá và tự nhân thức. Cô tạo ra những câu chuyện đậm chất phiêu lưu, khám phá những địa điểm kỳ lạ và khám phá sự thay đổi của nhân vật chính. Eva nổi tiếng với việc tạo ra những môi trường sống sống động và tạo nên những câu chuyện kích thích trí tưởng tượng của độc giả.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247907/authors/q3dr9wecsfyn16qdayak.jpg'),
       (10, 'Natalia Herrera', 'Natalia Herrera là một nhà văn người Mexico nổi tiếng với những tác phẩm về lịch sử và văn hóa của quê hương. Ông sử dụng lịch sử rực rỡ và truyền thống đa dạng của Mexico để tạo ra những câu chuyện đặc sắc về sức mạnh của tình yêu và sự khám phá. Tác phẩm của Natalia thường chứa đựng những câu chuyện rộng lớn và đan xen giữa thực tế và huyền bí.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247907/authors/exjoihbzcza9zhvtcgm4.jpg'),
       (11, 'Nguyễn Thị Lan', 'Nguyễn Thị Lan là một nhà văn Việt Nam nổi tiếng với những tác phẩm về cuộc sống nông thôn và tình yêu thiên nhiên. Cô tạo ra những câu chuyện mộc mạc và chân thực về cuộc sống của những người dân làng quê, thể hiện sự quan tâm và tình yêu của mình đối với văn hóa và truyền thống dân tộc.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247907/authors/bb6jjeh3em63c3dxaf9s.jpg'),
       (12, 'Trần Văn Hải', 'Trần Văn Hải là một nhà văn người Việt Nam được biết đến với những tác phẩm về lịch sử và cuộc sống trong thời kỳ chiến tranh. Ông tạo ra những câu chuyện sâu sắc và cảm động về những khó khăn và đau khổ mà con người phải đối mặt trong những thời điểm khó khăn nhất.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247907/authors/dpjfmrcyyyyxq7rkwcfh.jpg'),
       (13, 'Lê Văn Anh', 'Lê Văn Anh là một nhà văn nổi tiếng của Việt Nam với những tác phẩm về tình yêu và mối quan hệ con người. Ông tạo ra những câu chuyện đầy cảm xúc và sâu sắc về tình yêu, gia đình và những mất mát trong cuộc sống. Các tác phẩm của Lê Văn Anh thường mang thông điệp về tình yêu, sự tha thứ và hy vọng.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247907/authors/v00rwrtm2tita5xzhzcz.jpg'),
       (14, 'Phạm Văn Đức', 'Phạm Văn Đức là một nhà văn Việt Nam nổi tiếng với những tác phẩm về cuộc sống thành thị và thách thức mà người dân đô thị phải đối mặt. Ông tạo ra những câu chuyện sắc nét và sôi động về những mối quan hệ xã hội và những khía cạnh khác nhau của cuộc sống đô thị.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247907/authors/v00rwrtm2tita5xzhzcz.jpg'),
       (15, 'Nguyễn Thanh Hương', 'Nguyễn Thanh Hương là một nhà văn Việt Nam được biết đến với những tác phẩm về tình yêu và sự trưởng thành. Cô tạo ra những câu chuyện cảm động và lôi cuốn về sự tìm kiếm bản thân, sự đối mặt với khó khăn và hành trình tìm kiếm ý nghĩa cuộc sống.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247906/authors/ypwnespznk1mzkar2bpi.jpg'),
       (16, 'Hoàng Văn Tùng', 'Hoàng Văn Tùng là một nhà văn người Việt Nam nổi tiếng với những tác phẩm về lịch sử và văn hóa Việt Nam. Ông tạo ra những câu chuyện chi tiết và chân thực về những giai đoạn quan trọng trong lịch sử Việt Nam và sự ảnh hưởng của chúng đối với cuộc sống của con người.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247907/authors/v00rwrtm2tita5xzhzcz.jpg'),
       (17, 'Đinh Thị Thanh', 'Đinh Thị Thanh là một nhà văn nổi tiếng của Việt Nam với những tác phẩm về những vấn đề xã hội và văn hóa hiện đại. Cô tạo ra những câu chuyện sắc bén và tinh tế về cuộc sống của những người trẻ và những thách thức mà họ phải đối mặt trong xã hội đương đại.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247906/authors/bfce5uamppjlu0asfhc7.jpg'),
       (18, 'Vũ Thị Minh', 'Vũ Thị Minh là một nhà văn Việt Nam nổi tiếng với những tác phẩm về tình yêu và sự mất mát. Cô tạo ra những câu chuyện đẹp và sâu sắc về tình yêu, sự hy sinh và lòng trung thành. Các tác phẩm của Vũ Thị Minh thường mang thông điệp về giá trị của tình người và lòng nhân ái.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247906/authors/afqm1thws99ozszlnpzq.jpg'),
       (19, 'Nguyễn Thị Hạnh', 'Nguyễn Thị Hạnh là một nhà văn Việt Nam nổi tiếng với những tác phẩm về những vấn đề xã hội như bạo lực gia đình, nhân quyền và sự bất công. Cô tạo ra những câu chuyện mạnh mẽ và đáng suy ngẫm về những vấn đề gây tranh cãi và ảnh hưởng đến cuộc sống của con người.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247906/authors/zk2osz3vdr1ct02fgu8m.jpg'),
       (20, 'Trần Thị Thanh Nhàn', 'Trần Thị Thanh Nhàn là một nhà văn Việt Nam nổi tiếng với những tác phẩm về cuộc sống vùng nông thôn và sự thay đổi của nông nghiệp. Cô tạo ra những câu chuyện chân thực và cảm động về cuộc sống của những người nông dân và sự đối mặt với những thách thức môi trường và kinh tế.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247906/authors/mb3pckoi3l5bxo57wb5d.jpg'),
       (21, 'Trần Văn Mai', 'Trần Văn Mai là một nhà văn Việt Nam nổi tiếng với những tác phẩm về cuộc sống thành thị và sự đấu tranh vì công bằng xã hội.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247907/authors/amld88fm1ugwf9yhg5ey.jpg'),
       (22, 'Nguyễn Minh Châu', 'Nguyễn Minh Châu là một nhà văn nổi tiếng của Việt Nam với những tác phẩm về tình yêu và sự khám phá bản thân.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247906/authors/q8f0aq57qjj4xoewa8j8.jpg'),
       (23, 'Phạm Văn Long', 'Phạm Văn Long là một nhà văn Việt Nam được biết đến với những tác phẩm về lịch sử và văn hóa dân tộc.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247906/authors/vzqaictvvgtjnd3metcq.jpg'),
       (24, 'Lê Thị Thuận', 'Lê Thị Thuận là một nhà văn Việt Nam nổi tiếng với những tác phẩm về tình yêu và sự hy sinh.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247906/authors/afqm1thws99ozszlnpzq.jpg'),
       (25, 'Nguyễn Hải Yến', 'Nguyễn Hải Yến là một nhà văn người Việt Nam được biết đến với những tác phẩm về cuộc sống xã hội và bất bình đẳng.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247906/authors/zk2osz3vdr1ct02fgu8m.jpg'),
       (26, 'Trần Thị Thanh Hằng', 'Trần Thị Thanh Hằng là một nhà văn nổi tiếng của Việt Nam với những tác phẩm về sự đấu tranh cho quyền tự do và công lý.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247906/authors/mb3pckoi3l5bxo57wb5d.jpg'),
       (27, 'Nguyễn Văn Hoàn', 'Nguyễn Văn Hoàn là một nhà văn Việt Nam nổi tiếng với những tác phẩm về tình yêu và tâm lý con người.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247905/authors/jyobicok8lfzkcmnmnht.jpg'),
       (28, 'Lê Thị Thanh Tâm', 'Lê Thị Thanh Tâm là một nhà văn Việt Nam được biết đến với những tác phẩm về cuộc sống thanh xuân và tình yêu tuổi trẻ.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247906/authors/gaqffk7egwxhk3gyelvr.jpg'),
       (29, 'Trần Đình Tùng', 'Trần Đình Tùng là một nhà văn Việt Nam nổi tiếng với những tác phẩm về lịch sử và truyền thống văn hóa.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247905/authors/jrjho249ihtbmx5vxerl.jpg'),
       (30, 'Nguyễn Thị Hồng', 'Nguyễn Thị Hồng là một nhà văn người Việt Nam được biết đến với những tác phẩm về cuộc sống xã hội và tình yêu gia đình.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247906/authors/qkrzpgejdgo5sdxlwvii.jpg'),
       (31, 'Sophia Anderson', 'Sophia Anderson là một nhà văn nổi tiếng nước ngoài được biết đến với những cuốn tiểu thuyết hấp dẫn mang tính bí ẩn. Cô tạo nên những cốt truyện phức tạp và những nhân vật lôi cuốn, khiến người đọc không thể rời mắt.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247906/authors/cg74aykbvzhkn3hupw9o.jpg'),
      (32, 'Daniel Garcia', 'Daniel Garcia là một nhà văn nổi tiếng nước ngoài được biết đến với những tác phẩm văn học đầy ý nghĩa. Công trình của ông khám phá những cung bậc phức tạp của cảm xúc con người và đi vào những chủ đề sâu sắc về tồn tại, để lại trong lòng người đọc những hiểu biết sâu sắc.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247905/authors/qefgashvaoiwtho1tkrm.jpg'),
      (33, 'Olivia Roberts', 'Olivia Roberts là một nhà văn danh tiếng nước ngoài được công nhận với những cuốn tiểu thuyết lịch sử. Cô nghiên cứu cẩn thận về các giai đoạn lịch sử khác nhau và đem chúng trở nên sống động thông qua miêu tả tinh tế và cách kể chuyện giàu cảm xúc.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247905/authors/nwl4wstxvbhperoppada.jpg'),
      (34, 'Maximilian Müller', 'Maximilian Müller là một nhà văn nổi tiếng nước ngoài được biết đến với những cuốn tiểu thuyết khoa học viễn tưởng và thể loại hậu tận thế. Tác phẩm của ông đưa người đọc đến những thế giới tưởng tượng phong phú, thách thức các quy tắc xã hội và khám phá những khả năng của tương lai.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247904/authors/gxkggfqaefkzjvxda498.jpg'),
      (35, 'Elena Petrova', 'Elena Petrova là một nhà văn nổi tiếng nước ngoài được biết đến với những tuyển tập thơ sâu sắc. Lời của cô chạm đến trái tim và lan tỏa sâu sắc, lôi cuốn bản chất của cảm xúc và vẻ đẹp của trải nghiệm con người.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247905/authors/lqkrslzkpbygw70ezyib.jpg'),
      (36, 'Lucas Moreau', 'Lucas Moreau là một nhà văn nổi tiếng nước ngoài được công nhận với loạt tiểu thuyết giả tưởng bán chạy nhất. Cô tạo ra những hệ thống thần thoại phức tạp và những thế giới giả tưởng sống động, quyến rũ độc giả mọi lứa tuổi.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247905/authors/n28wakjfi2pn1m6tjvkb.jpg'),
      (37, 'Mia Sanchez', 'Mia Sanchez là một nhà văn danh tiếng nước ngoài được biết đến với những tiểu thuyết kịch tính tâm lý đầy hấp dẫn. Cô tạo nên những cốt truyện gây căng thẳng và khám phá sâu trong tâm lý con người, để lại trong lòng người đọc những ấn tượng mê hoặc.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247905/authors/n28wakjfi2pn1m6tjvkb.jpg'),
      (38, 'Raphael Klein', 'Raphael Klein là một nhà văn nổi tiếng nước ngoài được biết đến với những tác phẩm văn học tâm đắc. Tiểu thuyết của cô khám phá những chủ đề tình yêu, mất mát và nhận thức cá nhân, thu hút người đọc bằng văn chương trữ tình và cách kể chuyện sâu sắc.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247905/authors/rnt4cjqqua4tblpjls29.jpg'),
      (39, 'Isabella Costa', 'Isabella Costa là một nhà văn nổi tiếng nước ngoài được công nhận với những bộ tiểu thuyết lịch sử hùng ép. Cô tài hoa trong việc kết hợp nhiều thế hệ và câu chuyện, đưa người đọc vào những câu chuyện tình yêu, chiến tranh và số phận lớn lao.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247904/authors/qbtizxcuztid7vkl3apq.jpg'),
      (40, 'Anton Volkov', 'Anton Volkov là một nhà văn nổi tiếng nước ngoài được biết đến với những cuốn tiểu thuyết kịch tính tội phạm. Các tác phẩm của ông đầy những bất ngờ và nút thắt, khiến người đọc luôn băn khoăn đến phút cuối cùng.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247904/authors/ae5lndymshxaxpjqbhs6.jpg'),
      (41, 'Emma Johansson', 'Emma Johansson là một nhà văn danh tiếng nước ngoài được biết đến với những cuốn tiểu thuyết tình yêu đương đại ấm áp. Câu chuyện của cô lấy điềm tĩnh, phức tạp của tình yêu và quan hệ, để lại trong lòng người đọc một cảm giác ấm áp và đầy cảm động.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247904/authors/fza0mwviccydt9ppe6x7.jpg'),
      (42, 'Sebastian Müller', 'Sebastian Müller là một nhà văn tài năng nước ngoài được công nhận với những cuốn sách phi hư cấu thông minh. Ông đi sâu vào nhiều chủ đề, mang đến cho người đọc những quan điểm quý giá và cái nhìn gợi mở để suy ngẫm.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247904/authors/dv8wxrppe1kpmszphu7q.jpg'),
      (43, 'Alicia Fernandez', 'Alicia Fernandez là một nhà văn nổi tiếng nước ngoài được biết đến với những tác phẩm văn học mạnh mẽ, khám phá các vấn đề xã hội và chính trị với sự sâu sắc và nhạy cảm.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247904/authors/fza0mwviccydt9ppe6x7.jpg'),
      (44, 'Leonardo Rossi', 'Leonardo Rossi là một nhà văn danh tiếng nước ngoài được biết đến với những tiểu thuyết thực tế kỳ diệu hấp dẫn, đưa người đọc vào thế giới phi thực tế đầy màu sắc.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247904/authors/dxmg8khmlutqdo43wpi7.jpg'),
      (45, 'Emily Watson', 'Emily Watson là một nhà văn nổi tiếng nước ngoài được công nhận với những câu chuyện tận cùng tình cảm và sâu sắc, lôi cuốn người đọc với những câu chuyện chân thành.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247904/authors/jfnssmgucg3h4jr2ffpc.jpg'),
      (46, 'Alexander Weber', 'Alexander Weber là một nhà văn tài năng nước ngoài được biết đến với những cuốn tiểu thuyết lịch sử hấp dẫn, mang đến cái nhìn độc đáo về các sự kiện quan trọng trong lịch sử.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247903/authors/lxfvq03slicr8cv26mhq.jpg'),
      (47, 'Sophie Martin', 'Sophie Martin là một nhà văn nổi tiếng nước ngoài được biết đến với những cuốn tiểu thuyết hồi hộp và ly kỳ. Cô tạo ra những câu chuyện đan xen phức tạp và những nhân vật đa chiều, khiến người đọc bị cuốn hút vào từng trang sách.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247903/authors/nplwwlotb0gbhgz6ftlf.jpg'),
      (48, 'Gabriel Torres', 'Gabriel Torres là một nhà văn danh tiếng nước ngoài được biết đến với những tác phẩm văn học sâu sắc. Công trình của ông khám phá những khía cạnh phức tạp của con người và đặt câu hỏi về ý nghĩa của cuộc sống.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247903/authors/nplwwlotb0gbhgz6ftlf.jpg'),
      (49, 'Amelia Schneider', 'Amelia Schneider là một nhà văn nổi tiếng nước ngoài được công nhận với những cuốn tiểu thuyết lịch sử tuyệt vời. Cô kết hợp lịch sử và hư cấu một cách tài tình, mang lại cho người đọc những câu chuyện đầy cảm xúc và hấp dẫn.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247903/authors/r0eyjeb80tfgqp4r75mi.jpg'),
      (50, 'Oliver Fischer', 'Oliver Fischer là một nhà văn tài năng nước ngoài được biết đến với những tiểu thuyết tâm lý độc đáo. Công trình của cô khám phá những tình huống phức tạp và mở ra những tri thức mới về tâm lý con người.', 'https://res.cloudinary.com/dvmns3t6h/image/upload/v1689247903/authors/wc7zpdtbsdo57vvqn1au.jpg');


