INSERT INTO `supplier` (`supplier_id`, `supplier_name`, `supplier_description`) VALUES
      (1, 'Văn Học Ảo', 'Văn Học Ảo là một nhà xuất bản hư cấu nổi tiếng với danh mục sách đa dạng từ tiểu thuyết, văn học kinh điển đến sách hướng dẫn và tự truyện. Nhà xuất bản này nổi tiếng với sự tôn trọng cao về chất lượng và việc ủng hộ các tác giả mới nổi.'),
      (2, 'Thiên Long Books', 'Thiên Long Books là một nhà xuất bản hư cấu phát triển tại thị trường văn học giả tưởng. Với sứ mệnh khám phá các tác phẩm độc đáo và kích thích trí tưởng tượng, Thiên Long Books đang là điểm đến lý tưởng cho các tác giả văn học giả tưởng.'),
      (3, 'Ngôi Sao Sáng', 'Ngôi Sao Sáng là một nhà xuất bản hư cấu chuyên về văn học trẻ em và tuổi teen. Nhà xuất bản này tạo ra những cuốn sách thú vị và hấp dẫn, mang đến niềm vui và sự khám phá cho độc giả trẻ.'),
      (4, 'Văn Nghệ Viễn Tưởng', 'Văn Nghệ Viễn Tưởng là một nhà xuất bản hư cấu chuyên về văn học khoa học viễn tưởng và thể loại hậu tận thế. Nhà xuất bản này phục vụ cộng đồng người hâm mộ văn học viễn tưởng với những tác phẩm sáng tạo và độc đáo.'),
      (5, 'Hoàng Gia Publishing', 'Hoàng Gia Publishing là một nhà xuất bản hư cấu nổi tiếng với sự chuyên nghiệp và chất lượng sách. Nhà xuất bản này đặt mục tiêu trở thành điểm đến hàng đầu cho những tác giả văn học uy tín và những tác phẩm đáng chú ý.'),
      (6, 'Phương Nam Book', 'Phương Nam Book là một nhà xuất bản hư cấu chuyên về văn học và văn hóa Việt Nam. Nhà xuất bản này cam kết giữ vững và phát triển di sản văn hóa, đồng thời tạo điều kiện cho các tác giả mới nổi tiếng có cơ hội truyền thông và phát triển sự nghiệp.'),
      (7, 'Nắng Sáng Publishing', 'Nắng Sáng Publishing là một nhà xuất bản hư cấu tập trung vào việc xuất bản các tác phẩm văn học về tự truyện, tiểu thuyết và thơ ca. Nhà xuất bản này đề cao chất lượng và tìm kiếm những giọng điệu văn học độc đáo.'),
      (8, 'Đông Tây Publishing', 'Đông Tây Publishing là một nhà xuất bản hư cấu đa quốc gia với danh mục sách đa dạng. Nhà xuất bản này tập trung vào việc giới thiệu những tác phẩm văn học đặc sắc từ nhiều nền văn hóa và chia sẻ những giá trị văn hóa đa dạng.'),
      (9, 'Thiên Đường Đọc Sách', 'Thiên Đường Đọc Sách là một nhà xuất bản hư cấu đặc biệt dành cho trẻ em. Nhà xuất bản này tạo ra những cuốn sách giáo dục và giải trí phù hợp với lứa tuổi, mang đến cho trẻ em niềm vui và tình yêu đọc sách.'),
      (10, 'Văn Hóa Việt', 'Văn Hóa Việt là một nhà xuất bản hư cấu uy tín với tầm nhìn lâu dài về việc tôn trọng và bảo tồn di sản văn hóa. Nhà xuất bản này tạo ra những cuốn sách mang giá trị văn hóa và đóng góp vào sự phát triển văn hóa của xã hội.'),
      (11, 'Nguyên Tác Books', 'Nguyên Tác Books là một nhà xuất bản hư cấu nổi tiếng với việc chuyển ngữ và xuất bản các tác phẩm văn học từ ngôn ngữ gốc sang ngôn ngữ khác. Nhà xuất bản này giới thiệu độc giả với những tác phẩm đáng chú ý từ khắp nơi trên thế giới.'),
      (12, 'Hòa Bình Publishing', 'Hòa Bình Publishing là một nhà xuất bản hư cấu tập trung vào việc xuất bản các tác phẩm văn học mang thông điệp về hòa bình và lý tưởng nhân loại. Nhà xuất bản này góp phần xây dựng một thế giới tốt đẹp qua trang sách.'),
      (13, 'Đất Việt Books', 'Đất Việt Books là một nhà xuất bản hư cấu với mục tiêu giới thiệu và tôn vinh văn hóa Việt Nam. Nhà xuất bản này cung cấp những cuốn sách đa dạng về văn hóa, lịch sử, nghệ thuật và truyền thống của Việt Nam.'),
      (14, 'Thiên Phúc Books', 'Thiên Phúc Books là một nhà xuất bản hư cấu chuyên về sách tham khảo và hướng dẫn. Nhà xuất bản này cung cấp cho độc giả những tài liệu chất lượng cao để học tập và nâng cao kiến thức.'),
      (15, 'Phương Đông Publishing', 'Phương Đông Publishing là một nhà xuất bản hư cấu với trọng tâm là văn hóa và văn học phương Đông. Nhà xuất bản này tập trung vào việc giới thiệu cho độc giả những tác phẩm độc đáo từ các nền văn hóa phương Đông.'),
      (16, 'Văn Minh Books', 'Văn Minh Books là một nhà xuất bản hư cấu chuyên về sách giáo dục và văn hóa. Nhà xuất bản này tạo ra những tác phẩm giáo dục sáng tạo và truyền cảm hứng cho độc giả mọi lứa tuổi.'),
      (17, 'Vĩnh Hưng Publishing', 'Vĩnh Hưng Publishing là một nhà xuất bản hư cấu tập trung vào việc xuất bản các tác phẩm văn học kinh điển và sách lịch sử. Nhà xuất bản này mang đến cho độc giả những cuốn sách giá trị và quan trọng về mặt văn hóa và lịch sử.'),
      (18, 'Nhân Văn Books', 'Nhân Văn Books là một nhà xuất bản hư cấu với sứ mệnh truyền tải tri thức và văn hóa đến độc giả. Nhà xuất bản này đặc biệt chú trọng đến các tác phẩm chất lượng trong các lĩnh vực như triết học, khoa học xã hội và nghệ thuật.'),
      (19, 'Tinh Hoa Publishing', 'Tinh Hoa Publishing là một nhà xuất bản hư cấu tạo ra những tác phẩm văn học mang tính chất tưởng tượng và sáng tạo. Nhà xuất bản này thúc đẩy sự phát triển của văn chương mới và khám phá những giá trị văn hóa độc đáo.'),
      (20, 'Sáng Tạo Books', 'Sáng Tạo Books là một nhà xuất bản hư cấu đặc biệt tập trung vào việc xuất bản các tác phẩm sáng tạo và đột phá. Nhà xuất bản này khuyến khích sự sáng tạo và ủng hộ những tác giả mang lại cái mới và khác biệt trong văn học.'),
